INSERT INTO `wp_trandaiposts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-01-22 07:35:31', '2013-01-22 07:35:31', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2013-02-20 09:07:44', '2013-02-20 09:07:44', '', 0, 'http://enpii-03/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2013-01-22 07:35:31', '2013-01-22 07:35:31', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://enpii-03/wordpress/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2013-01-22 07:35:31', '2013-01-22 07:35:31', '', 0, 'http://enpii-03/wordpress/?page_id=2', 0, 'page', '', 0),
(4, 1, '2013-01-22 08:03:22', '2013-01-22 08:03:22', '<h3><span style="color: #ff6600;">Cầu vượt thép tại ngã tư Hàng Xanh và Thủ Đức đang bước vào giai đoạn hoàn tất. Các làn xe sẽ được lên cầu từ 27/1 tới, góp phần giải quyết ách tắc giao thông ngay trước Tết Nguyên đán.</span><!--more--></h3>\r\n<table width="90%" border="0" cellspacing="0" cellpadding="3" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon_01.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Sau Hà Nội, TP HCM xây dựng các cầu vượt thép. Với ưu thế lắp đặt nhanh, những cầu vượt mới này được kỳ vọng sẽ giải quyết tình hình ùn tắc giao thông trong giờ cao điểm tại các ngã tư lớn trên xa lộ Hà Nội.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__02.JPG" width="490" height="284" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Tại ngã tư Hàng Xanh, cầu vượt khá gần trung tâm thành phố đang trong giai đoạn hoàn thiện. Cầu rộng 16 m với 4 làn xe chạy 2 chiều dành cho xe buýt và ôtô dưới 9 chỗ.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__13.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Trong khi đó, tiến độ làm việc tại ngã tư Thủ Đức cơ bản đã hoàn thành. Mặt đường hai bên thành cầu và giải phân cách giữa các làn xe đã hoàn tất.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__03.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Công nhân lắp rắp những bộ phận cuối cùng trên thành cầu vượt Hàng Xanh.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__04.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Phía dưới hệ thống cáp quang, chiếu sáng trong khu vực cầu vượt cũng đang được khẩn trương hoàn tất.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__07.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Hàng rào thép ngăn cách hai làn xe ngược chiều được làm bằng những ống sắt cỡ lớn, kiên cố.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__10.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Những chuyến xe chở nguyên vật liệu cuối cùng hoàn tất cầu vượt. Dù lắp đặt nhanh, nhưng theo đơn vị thi công, cầu sẽ chịu được sức tải lớn của các loại xe.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__11.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Khu vực ngã tư Thủ Đức là một trong những điểm nóng về giao thông tại TP HCM.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__14.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Ngã tư Hàng Xanh cũng là tuyến đường trọng điểm trên quốc lộ 1A, lượng xe lưu thông lớn nhưng lại đi qua các khu vực đông dân cư.</td>\r\n</tr>\r\n<tr>\r\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__15.JPG" width="490" height="325" border="1" /></td>\r\n</tr>\r\n<tr>\r\n<td>Dự án hai cầu vượt Hàng Xanh và Ngã tư Thủ Đức sẽ chính thức đưa vào sử dụng ngày 27/1/2013 tới đây.</td>\r\n</tr>\r\n</tbody>\r\n</table>', 'Cầu vượt thép đầu tiên ở Sài Gòn trước ngày thông xe', '', 'publish', 'open', 'open', '', 'cau-vuot-thep-dau-tien-o-sai-gon-truoc-ngay-thong-xe', '', '', '2013-01-29 02:52:24', '2013-01-29 02:52:24', '', 0, 'http://enpii-03/wordpress/?p=4', 0, 'post', '', 0),
(5, 1, '2013-01-22 08:03:19', '2013-01-22 08:03:19', '<h2>Cầu vượt thép tại ngã tư Hàng Xanh và Thủ Đức đang bước vào giai đoạn hoàn tất. Các làn xe sẽ được lên cầu từ 27/1 tới, góp phần giải quyết ách tắc giao thông ngay trước Tết Nguyên đán.<!--more--></h2>\n<table width="90%" border="0" cellspacing="0" cellpadding="3" align="center">\n<tbody>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon_01.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Sau Hà Nội, TP HCM xây dựng các cầu vượt thép. Với ưu thế lắp đặt nhanh, những cầu vượt mới này được kỳ vọng sẽ giải quyết tình hình ùn tắc giao thông trong giờ cao điểm tại các ngã tư lớn trên xa lộ Hà Nội.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__02.JPG" width="490" height="284" border="1" /></td>\n</tr>\n<tr>\n<td>Tại ngã tư Hàng Xanh, cầu vượt khá gần trung tâm thành phố đang trong giai đoạn hoàn thiện. Cầu rộng 16 m với 4 làn xe chạy 2 chiều dành cho xe buýt và ôtô dưới 9 chỗ.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__13.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Trong khi đó, tiến độ làm việc tại ngã tư Thủ Đức cơ bản đã hoàn thành. Mặt đường hai bên thành cầu và giải phân cách giữa các làn xe đã hoàn tất.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__03.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Công nhân lắp rắp những bộ phận cuối cùng trên thành cầu vượt Hàng Xanh.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__04.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Phía dưới hệ thống cáp quang, chiếu sáng trong khu vực cầu vượt cũng đang được khẩn trương hoàn tất.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__07.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Hàng rào thép ngăn cách hai làn xe ngược chiều được làm bằng những ống sắt cỡ lớn, kiên cố.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__10.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Những chuyến xe chở nguyên vật liệu cuối cùng hoàn tất cầu vượt. Dù lắp đặt nhanh, nhưng theo đơn vị thi công, cầu sẽ chịu được sức tải lớn của các loại xe.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__11.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Khu vực ngã tư Thủ Đức là một trong những điểm nóng về giao thông tại TP HCM.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__14.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Ngã tư Hàng Xanh cũng là tuyến đường trọng điểm trên quốc lộ 1A, lượng xe lưu thông lớn nhưng lại đi qua các khu vực đông dân cư.</td>\n</tr>\n<tr>\n<td><img alt="" src="http://vnexpress.net/Files/Subject/3b/be/0f/4d/cau_thep_sai_gon__15.JPG" width="490" height="325" border="1" /></td>\n</tr>\n<tr>\n<td>Dự án hai cầu vượt Hàng Xanh và Ngã tư Thủ Đức sẽ chính thức đưa vào sử dụng ngày 27/1/2013 tới đây.</td>\n</tr>\n</tbody>\n</table>', 'Cầu vượt thép đầu tiên ở Sài Gòn trước ngày thông xe', '', 'inherit', 'open', 'open', '', '4-revision', '', '', '2013-01-22 08:03:19', '2013-01-22 08:03:19', '', 4, 'http://enpii-03/wordpress/?p=5', 0, 'revision', '', 0),
